ValidationRule.errorConditionFormula:
AND(
OR(
    RecordType.Name ='Client',
    RecordType.Name ='Bill To Client'
),
    NOT(ISBLANK(ClientAddress__Street__s)),
    NOT(
        AND(
            CONTAINS(ClientAddress__Street__s, ','),
            REGEX(ClientAddress__Street__s, ".*[a-zA-Z0-9].*,.*[a-zA-Z0-9].*")


       ) 
   )
)
